mapper.update:
UPDATE tbl_emp
SET emp_name = #{emp_name}, gender = #{gender },phone= #{ phone},email= #{ email},birth= to_char(#{birth},'yyyy-MM-dd'),address= #{address,jdbcType=VARCHAR}
,admin= #{admin}, profile_path=#{profile_path,jdbcType=VARCHAR}
WHERE emp_no= #{emp_no}

UPDATE tbl_org
SET rank_code =#{rank_name}, branch_code=#{branch_name}, dept_code=#{department_name}
WHERE emp_no= #{emp_no}

mapper.select:
select emp_no
from tbl_emp
where emp_name=#{emp_name} and email=#{email}

select emp_name
from tbl_emp
where emp_no=#{emp_no}

mapper.update:
UPDATE tbl_org
